benchmarkPrint(title: "\nMersenne to exp 128")
{
	// Mersenne to exp 100 Debug Mode
	// 27.01.16: 2559ms
	// 30.01.16: 2092ms
	// 04.02.16: 1330ms
	// 06.02.16: 1143ms

	// Mersenne to exp 128 Debug Mode
	// 06.02.16: 2853ms
	// 10.02.16: 2804ms
	// 20.02.16: 1838ms
	// 28.02.16: 1665ms
	// 07.03.16: 1643ms
	// 07.03.16: 1227ms

	var isM = false
	for i in 1...128
	{
		isM = isMersenne(i)
		if isM { print(i, terminator: ", ") }
	}
}


